mapper.insert:
/* snippBbs.insert */
INSERT INTO SNP_BBS (
     BBS_TP
    ,BBS_NM
    ,USE_AT
    ,DELETE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{bbsTp}
    ,#{bbsNm}
    ,#{useAt}
    ,'N'
    ,#{regUid}
    ,CURRENT_TIMESTAMP
    ,#{updtUid}
    ,CURRENT_TIMESTAMP
)

mapper.update:
/* snippBbs.update */
UPDATE SNP_BBS
   SET BBS_NM = #{bbsNm}
      ,USE_AT = #{useAt}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = CURRENT_TIMESTAMP
 WHERE UID = #{uid}

mapper.select:
/* snippBbs.page */ SELECT CONCAT('MAIN', '_', SB.UID) AS MAIN_ID ,SB.UID AS BBS_UID ,SB.BBS_TP ,SB.BBS_NM ,SB.USE_AT ,SB.DELETE_AT ,SB.REG_UID ,SB.REG_DT ,SU_REG.USER_ID AS REG_ID ,SU_REG.USER_NM AS REG_NM ,SB.UPDT_UID ,SB.UPDT_DT ,SU_UPDT.USER_ID AS UPDT_ID ,SU_UPDT.USER_NM AS UPDT_NM FROM SNP_BBS SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.BBS_NM LIKE CONCAT('%',#{bbsNm},'%')
AND SB.BBS_TP = #{bbsTp}
AND SB.USE_AT = #{useAt}
ORDER BY SB.REG_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snippBbs.list */ SELECT SB.UID AS BBS_UID ,SB.BBS_TP ,SB.BBS_NM ,SB.USE_AT ,SB.DELETE_AT ,SB.REG_UID ,SB.REG_DT ,SU_REG.USER_ID AS REG_ID ,SB.UPDT_UID ,SB.UPDT_DT ,SU_UPDT.USER_ID AS UPDT_ID FROM SNP_BBS SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.BBS_NM LIKE CONCAT('%',#{bbsNm},'%')
AND SB.BBS_TP = #{bbsTp}
AND SB.USE_AT = #{useAt}
ORDER BY SB.REG_DT DESC

/* snippBbs.count */
SELECT COUNT(*)
  FROM SNP_BBS SB
  LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID
  LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID
 WHERE 1 = 1
   AND SB.DELETE_AT = 'N'

   AND SB.BBS_NM LIKE CONCAT('%',#{bbsNm},'%')


   AND SB.BBS_TP = #{bbsTp}


   AND SB.USE_AT = #{useAt}

mapper.delete:
/* snippBbs.delete */
UPDATE SNP_BBS
   SET DELETE_AT = 'Y'
       ,UPDT_UID = #{updtUid}
       ,UPDT_DT = CURRENT_TIMESTAMP
  WHERE UID = #{uid}
