mapper.insert:
/* snippBbsCl.insert */
INSERT INTO SNP_BBS_CL (
     CL_NM
    ,UPPER_CL
    ,BBS_UID
    ,SORT_NO
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{clNm}
    ,#{upperCl}
    ,#{bbsUid}
    ,#{sortNo}
    ,#{regUid}
    ,CURRENT_TIMESTAMP
    ,#{updtUid}
    ,CURRENT_TIMESTAMP
)

mapper.update:
/* snippBbsCl.update */
UPDATE SNP_BBS_CL
   SET CL_NM = #{clNm}
      ,SORT_NO = #{sortNo}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

mapper.delete:
/* snippBbsCl.delete */
UPDATE SNP_BBS_CL
   SET DELETE_AT = 'Y'
 WHERE UID = #{uid}
