mapper.select:
/* snippBoard.countAllBoard */
SELECT COUNT(*)
  FROM SNP_BOARD
 WHERE BBS_UID = #{bbsUid}
   AND DELETE_AT = 'N'

mapper.insert:
/* snippBoard.insert */
INSERT INTO SNP_BOARD (
     BBS_UID
    ,TITLE
    ,CONTENT
    ,USE_AT
    ,THUMBNAIL_SRC
    ,FILE_UID
    ,CL_UID
    ,DELETE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{bbsUid}
    ,#{title}
    ,#{content}
    ,#{useAt}
    ,#{thumbnailSrc}
    ,#{fileUid}::bigint
    ,#{clUid}
    ,'N'
    ,#{regUid}
    ,CURRENT_TIMESTAMP
    ,#{updtUid}
    ,CURRENT_TIMESTAMP
)

mapper.update:
/* snippBoard.update */
UPDATE SNP_BOARD
   SET TITLE = #{title}
      ,CONTENT = #{content}
      ,USE_AT = #{useAt}
      ,FILE_UID = #{fileUid}::bigint
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = CURRENT_TIMESTAMP
 WHERE UID = #{uid}

mapper.select:
/* snippBoard.page */ SELECT SB.UID AS BOARD_UID ,SB.BBS_UID ,SB.TITLE ,SB.CONTENT ,SB.DELETE_AT ,SB.USE_AT ,SB.FILE_UID ,SB.REG_UID ,SB.REG_DT ,SU_REG.USER_ID AS REG_ID ,SU_REG.USER_NM AS REG_NM ,SB.UPDT_UID ,SB.UPDT_DT ,SU_UPDT.USER_ID AS UPDT_ID ,SU_UPDT.USER_NM AS UPDR_NM FROM SNP_BOARD SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.TITLE LIKE CONCAT('%',#{title},'%')
AND SB.BBS_UID = #{bbsUid}
AND SB.CL_UID = #{clUid}
ORDER BY SB.REG_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snippBoard.list */ SELECT SB.UID AS BOARD_UID ,SB.BBS_UID ,SB.TITLE ,SB.CONTENT ,SB.DELETE_AT ,SB.FILE_UID ,SB.REG_UID ,SB.REG_DT ,SU_REG.USER_ID AS REG_ID ,SB.UPDT_UID ,SB.UPDT_DT ,SU_UPDT.USER_ID AS UPDT_ID FROM SNP_BOARD SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.TITLE LIKE CONCAT('%',#{title},'%')
AND SB.BBS_UID = #{bbsUid}
AND SB.CL_UID = #{clUid}
ORDER BY SB.REG_DT DESC

/* snippBoard.count */
SELECT COUNT(*)
  FROM SNP_BOARD SB
  LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID
  LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID
 WHERE 1 = 1
   AND SB.DELETE_AT = 'N'

   AND SB.TITLE LIKE CONCAT('%',#{title},'%')


   AND SB.BBS_UID = #{bbsUid}


   AND SB.CL_UID = #{clUid}

mapper.delete:
/* snippBoard.delete */
UPDATE SNP_BOARD
   SET DELETE_AT = 'Y'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = CURRENT_TIMESTAMP
 WHERE UID = #{uid}

mapper.select:
/* snippBoard.countBoardUseCl */
SELECT COUNT(*)
  FROM SNP_BOARD
 WHERE DELETE_AT = 'N'
   AND CL_UID = #{clUid}
