mapper.select:
/* snipBoard.countAllBoard */
SELECT COUNT(*)
  FROM SNP_BOARD
 WHERE BBS_UID = #{bbsUid}
   AND DELETE_AT = 'N'

mapper.insert:
/* snipBoard.insert */
INSERT INTO SNP_BOARD (
     BBS_UID
    ,TITLE
    ,CONTENT
    ,DELETE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{bbsUid}
    ,#{title}
    ,#{content}
    ,'N'
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
)

mapper.update:
/* snipBoard.update */
UPDATE SNP_BOARD
   SET TITLE = #{title}
      ,CONTENT = #{content}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

mapper.select:
/* snipBoard.page */ SELECT SB.UID AS BOARD_UID ,SB.BBS_UID ,SB.TITLE ,SB.CONTENT ,SB.DELETE_AT ,SB.REG_UID ,SB.REG_DT ,SU_REG.USER_ID AS REG_ID ,SB.UPDT_UID ,SB.UPDT_DT ,SU_UPDT.USER_ID AS UPDT_ID FROM SNP_BOARD SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.TITLE LIKE CONCAT('%',#{title},'%')
AND SB.BBS_UID = #{bbsUid}
ORDER BY SB.REG_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snipBoard.list */ SELECT SB.UID AS BOARD_UID ,SB.BBS_UID ,SB.TITLE ,SB.CONTENT ,SB.DELETE_AT ,SB.REG_UID ,SB.REG_DT ,SU_REG.USER_ID AS REG_ID ,SB.UPDT_UID ,SB.UPDT_DT ,SU_UPDT.USER_ID AS UPDT_ID FROM SNP_BOARD SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.TITLE LIKE CONCAT('%',#{title},'%')
AND SB.BBS_UID = #{bbsUid}
ORDER BY SB.REG_DT DESC

/* snipBoard.count */ SELECT COUNT(*) FROM SNP_BOARD SB LEFT JOIN SNP_USER SU_REG ON SU_REG.UID = SB.REG_UID LEFT JOIN SNP_USER SU_UPDT ON SU_UPDT.UID = SB.UPDT_UID WHERE 1 = 1 AND SB.DELETE_AT = 'N'
AND SB.TITLE LIKE CONCAT('%',#{title},'%')
AND SB.BBS_UID = #{bbsUid}
ORDER BY SB.REG_DT DESC

mapper.delete:
/* snipBoard.delete */
UPDATE SNP_BOARD
   SET DELETE_AT = 'Y'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}
