mapper.insert:
/* snippComment.insert */
INSERT INTO SNP_COMMENT (
     BOARD_UID
    ,CONTENT
    ,DELETE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{boardUid}
    ,#{content}
    ,'N'
    ,#{regUid}
    ,CURRENT_TIMESTAMP
    ,#{updtUid}
    ,CURRENT_TIMESTAMP
)

mapper.update:
/* snippComment.update */
UPDATE SNP_COMMENT
   SET CONTENT = #{content}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = CURRENT_TIMESTAMP
 WHERE UID = #{uid}

mapper.delete:
/* snippComment.delete */
UPDATE SNP_COMMENT
   SET DELETE_AT = 'Y'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = CURRENT_TIMESTAMP
 WHERE UID = #{uid}
