mapper.insert:
/* snippLoginHist.insert */
INSERT INTO SNP_LOGIN_HIST (
     USER_EMAIL
    ,LOGIN_DT
    ,ACCESS_IP
    ,ACCESS_USER_AGENT
    ,LOGIN_ACCESS_TP
    ,LOGIN_ACCESS_DESCRIPTION
) VALUES (
     #{userEmail}
    ,NOW()
    ,#{accessIp}
    ,#{accessUserAgent}
    ,#{loginAccessTp}
    ,#{loginAccessDescription}
)

mapper.select:
/* snippLoginHist.page */ SELECT CONCAT('MAIN', '_', SNH.UID) AS MAIN_ID ,SNH.UID AS HIST_UID ,SNH.USER_EMAIL ,SNH.LOGIN_DT ,SNH.ACCESS_IP ,SNH.ACCESS_USER_AGENT ,SNH.LOGIN_ACCESS_TP ,SNH.LOGIN_ACCESS_DESCRIPTION FROM SNP_LOGIN_HIST SNH WHERE 1 = 1
AND SNH.LOGIN_DT BETWEEN #{startDate} AND #{endDate}
AND SNH.LOGIN_ACCESS_TP = #{loginAccessTp}
ORDER BY SNH.LOGIN_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snippLoginHist.list */ SELECT CONCAT('MAIN', '_', SNH.UID) AS MAIN_ID ,SNH.UID AS HIST_UID ,SNH.USER_EMAIL ,SNH.LOGIN_DT ,SNH.ACCESS_IP ,SNH.ACCESS_USER_AGENT ,SNH.LOGIN_ACCESS_TP ,SNH.LOGIN_ACCESS_DESCRIPTION FROM SNP_LOGIN_HIST SNH WHERE 1 = 1
AND SNH.LOGIN_DT BETWEEN #{startDate} AND #{endDate}
AND SNH.LOGIN_ACCESS_TP = #{loginAccessTp}
ORDER BY SNH.LOGIN_DT DESC

/* snippLoginHist.count */
SELECT COUNT(*)
  FROM SNP_LOGIN_HIST SNH
 WHERE 1 = 1

   AND SNH.LOGIN_DT BETWEEN #{startDate} AND #{endDate}


   AND SNH.LOGIN_ACCESS_TP = #{loginAccessTp}
