mapper.insert:
/* snippSUrlLog.insert */
INSERT INTO SNP_SHORTEN_URL_ACCESS_LOG (
     SHORT_URL
    ,USER_UID
    ,ACCESS_IP
    ,ACCESS_USER_AGENT
    ,ACCESS_DATE
    ,ACCESS_TP
) VALUES (
     #{shortUrl}
    ,#{userUid}
    ,#{accessIp}
    ,#{accessUserAgent}
    ,NOW()
    ,#{accessTp}
)

mapper.select:
/* snippSUrlLog.page */ SELECT SSUAL.UID AS LOG_UID ,SSUAL.SHORT_URL ,SSU.ORIGIN_URL AS ORIGIN_URL ,SSUAL.USER_UID ,SSUAL.ACCESS_IP ,SSUAL.ACCESS_USER_AGENT ,SSUAL.ACCESS_DATE ,SSUAL.ACCESS_TP FROM SNP_SHORTEN_URL_ACCESS_LOG SSUAL INNER JOIN SNP_SHORTEN_URL SSU ON SSU.SHORT_URL = SSUAL.SHORT_URL WHERE 1 = 1
AND SSUAL.ACCESS_DATE BETWEEN #{startDate} AND #{endDate}
AND SSUAL.ACCESS_TP = #{accessTp}
ORDER BY SSUAL.ACCESS_DATE DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snippSUrlLog.list */ SELECT SSUAL.UID AS LOG_UID ,SSUAL.SHORT_URL ,SSU.ORIGIN_URL AS ORIGIN_URL ,SSUAL.USER_UID ,SSUAL.ACCESS_IP ,SSUAL.ACCESS_USER_AGENT ,SSUAL.ACCESS_DATE ,SSUAL.ACCESS_TP FROM SNP_SHORTEN_URL_ACCESS_LOG SSUAL INNER JOIN SNP_SHORTEN_URL SSU ON SSU.SHORT_URL = SSUAL.SHORT_URL WHERE 1 = 1
AND SSUAL.ACCESS_DATE BETWEEN #{startDate} AND #{endDate}
AND SSUAL.ACCESS_TP = #{accessTp}
ORDER BY SSUAL.ACCESS_DATE DESC

/* snippSUrlLog.count */
SELECT COUNT(*)
  FROM SNP_SHORTEN_URL_ACCESS_LOG SSUAL
  INNER JOIN SNP_SHORTEN_URL SSU ON SSU.SHORT_URL = SSUAL.SHORT_URL
WHERE 1 = 1

  AND SSUAL.ACCESS_DATE BETWEEN #{startDate} AND #{endDate}


  AND SSUAL.ACCESS_TP = #{accessTp}
