mapper.insert:
/* snippShortUrl.insert */
INSERT INTO SNP_SHORTEN_URL (
     ORIGIN_URL
    ,SHORT_URL
    ,CREATED_AT
    ,EXPIRES_AT
    ,IS_PUBLIC
) VALUES (
     #{originUrl}
    ,''
    ,CURRENT_TIMESTAMP
    ,DATEADD('WEEK', 1, CURRENT_TIMESTAMP)
    ,#{isPublic}
)

mapper.select:
/* snippShortUrl.countByShortURL */
SELECT COUNT(*)
  FROM SNP_SHORTEN_URL
 WHERE SHORT_URL = #{shrotUrl}

mapper.update:
/* snippShortUrl.update */
UPDATE SNP_SHORTEN_URL
   SET SHORT_URL = #{shortUrl}
 WHERE UID = #{uid}

mapper.select:
/* snippShortUrl.page */ SELECT UID ,ORIGIN_URL ,SHORT_URL ,CREATED_AT ,EXPIRES_AT ,CASE WHEN (EXPIRES_AT >= CURRENT_TIMESTAMP) THEN 'N' WHEN (CURRENT_TIMESTAMP > EXPIRES_AT) THEN 'Y' END AS IS_EXPIRED ,IS_PUBLIC FROM SNP_SHORTEN_URL SSU WHERE 1 = 1
AND SSU.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND SSU.IS_PUBLIC = #{isPublic}
ORDER BY SSU.CREATED_AT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snippShortUrl.list */ SELECT UID ,ORIGIN_URL ,SHORT_URL ,CREATED_AT ,EXPIRES_AT ,CASE WHEN (EXPIRES_AT >= CURRENT_TIMESTAMP) THEN 'N' WHEN (CURRENT_TIMESTAMP > EXPIRES_AT) THEN 'Y' END AS IS_EXPIRED ,IS_PUBLIC FROM SNP_SHORTEN_URL SSU WHERE 1 = 1
AND SSU.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND SSU.IS_PUBLIC = #{isPublic}
ORDER BY SSU.CREATED_AT DESC

/* snippShortUrl.count */
SELECT COUNT(*)
  FROM SNP_SHORTEN_URL SSU
 WHERE 1 = 1

   AND SSU.CREATED_AT BETWEEN #{startDate} AND #{endDate}


   AND SSU.IS_PUBLIC = #{isPublic}
