mapper.select:
/* snippUser.countByUserEmail */
SELECT COUNT(*)
  FROM SNP_USER
 WHERE USER_EMAIL = #{userEmail}

/* snippUser.countByUserRole */
SELECT COUNT(*)
  FROM SNP_USER
 WHERE USER_ROLE = #{role}

/* snippUser.countByUserId */
SELECT COUNT(*)
  FROM SNP_USER
 WHERE USER_ID = #{userId}

mapper.insert:
/* snippUser.insert */
INSERT INTO SNP_USER (
     USER_ID
    ,PASSWORD
    ,USER_EMAIL
    ,USER_NM
    ,SOCIAL_UID
    ,USER_ROLE
    ,LAST_CONN_DT
    ,LAST_PWD_CHNG_DT
    ,PWD_FAIL_CNT
    ,IS_LOCKED
    ,IS_ENABLE
) VALUES (
     #{userId}
    ,#{password}
    ,#{userEmail}
    ,#{userNm}
    ,#{socialUid}
    ,#{userRole}
    ,CURRENT_TIMESTAMP
    ,DATEADD('DAY', 90, CURRENT_TIMESTAMP)
    ,0
    ,'N'
    ,'Y'
)

mapper.update:
/* snippUser.updateLastLoginDtAndPwdFailCntByUserId */
UPDATE SNP_USER
   SET PWD_FAIL_CNT = 0
      ,LAST_CONN_DT = CURRENT_TIMESTAMP
 WHERE USER_ID = #{userId}

/* snippUser.updateLastLoginDtAndPwdFailCntByUserEmail */
UPDATE SNP_USER
   SET PWD_FAIL_CNT = 0
      ,LAST_CONN_DT = CURRENT_TIMESTAMP
 WHERE USER_EMAIL = #{userEmail}

/* snippUser.updatePwdFailCnt */
UPDATE SNP_USER
   SET PWD_FAIL_CNT = #{pwdFailCnt}

       ,IS_LOCKED = 'Y'

  WHERE USER_ID = #{userId}

/* snippUser.updateUserPassword */
UPDATE SNP_USER
   SET PASSWORD = #{password}
      ,LAST_PWD_CHNG_DT = DATEADD('DAY', 90, CURRENT_TIMESTAMP)
 WHERE USER_ID = #{userId}

/* snippUser.updateUserChangePasswordDt */
UPDATE SNP_USER
   SET LAST_PWD_CHNG_DT = DATEADD('DAY', 90, CURRENT_TIMESTAMP)
 WHERE USER_ID = #{userId}

mapper.select:
/* snippUser.page */ SELECT SU.UID ,SU.USER_ID ,SU.PASSWORD ,SU.USER_EMAIL ,SU.USER_NM ,SU.USER_ROLE ,SU.LAST_CONN_DT ,SU.LAST_PWD_CHNG_DT ,SU.PWD_FAIL_CNT ,SU.IS_LOCKED ,SU.IS_ENABLE ,SSU.UID AS SOCIAL_UID ,SSU.SOCIAL_TP AS SOCIAL_TP ,SSU.USER_ID AS SOCIAL_USER_ID FROM SNP_USER SU LEFT JOIN SNP_SOCIAL_USER SSU ON SU.SOCIAL_UID = SSU.UID WHERE 1 = 1
AND SU.USER_NM LIKE CONCAT('%',#{userNm},'%')
AND SU.USER_EMAIL LIKE CONCAT('%',#{userEmail},'%')
ORDER BY SU.UID DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* snippUser.count */ SELECT COUNT(*) FROM SNP_USER SU LEFT JOIN SNP_SOCIAL_USER SSU ON SU.SOCIAL_UID = SSU.UID WHERE 1 = 1
AND SU.USER_NM LIKE CONCAT('%',#{userNm},'%')
AND SU.USER_EMAIL LIKE CONCAT('%',#{userEmail},'%')

mapper.update:
/* snippUser.updateUserLock */
UPDATE SNP_USER
   SET IS_LOCKED = #{isLocked}
 WHERE UID = #{uid}

/* snippUser.updateUserEnable */
UPDATE SNP_USER
   SET IS_ENABLE = #{isEnable}
 WHERE UID = #{uid}

/* snippUser.update */
UPDATE SNP_USER
   SET USER_EMAIL = #{userEmail}
      ,USER_NM = #{userNm}
      ,PASSWORD = #{password}
 WHERE UID = #{uid}
